DROP DATABASE IF EXISTS bkap;
CREATE DATABASE bkap CHARACTER SET utf8 COLLATE utf8_unicode_ci;
use bkap;
Create table category
(
  id int primary key auto_increment,
  name varchar(100) not null unique,
  ordering int default '0',
  status tinyint(1) default '0'
);

Create table product
(
  id int primary key auto_increment,
  name varchar(150) not null,
  price int not null default '0',
  sale_price int null default '0',
  image varchar(150) null,
  content text null,
  category_id int not null
);

alter table product add foreign key 
FK_product_category (category_id) references category(id);

Create table account
(
  id int primary key auto_increment,
  name varchar(150) not null,
  email varchar(150) not null UNIQUE,
  phone varchar(20) not null UNIQUE,
  address varchar(250) not null,
  password varchar(64) not null,
  type tinyint null default '0'
);

Create table orders
(
  id int primary key auto_increment,
  account_id int null default '0',
  name varchar(150) null,
  email varchar(150)  null,
  phone varchar(20)  null,
  address varchar(250) null,
  promo varchar(8) NULL,
  status tinyint null default '0',
  orders_date timestamp default CURRENT_TIMESTAMP
);

alter table orders add foreign key 
FK_orders_account (account_id) references account(id);

Create table order_product
(
  order_id int not null,
  product_id int not null,
  price int not null,
  quantity int not null,
  primary key (order_id,product_id)
);

alter table order_product add foreign key 
FK_orders_order_product (order_id) references orders(id) ON DELETE CASCADE;
alter table order_product add foreign key 
FK_product_order_product (product_id) references product(id) ON DELETE CASCADE;

Create table promotion
(
  id int primary key auto_increment,
  name varchar(150) not null,
  value int not null,
  code varchar(8) NOT NULL UNIQUE
);

